import React, { useState, useRef, useEffect } from 'react';
import './ChatAgent.css';
import { motion, AnimatePresence } from 'framer-motion';
import { aiService } from '../../services/aiService';

const ChatAgent = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const [isTyping, setIsTyping] = useState(false);
    const messagesEndRef = useRef(null);
    const [learningStyle, setLearningStyle] = useState(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!input.trim()) return;

        // Add user message
        const userMessage = { text: input, sender: 'user' };
        setMessages(prev => [...prev, userMessage]);
        setInput('');
        setIsTyping(true);

        try {
            // Get chat response
            const response = await aiService.chatWithAgent(input);
            
            // Analyze learning style
            const styleAnalysis = await aiService.analyzeLearningStyle(input);
            if (styleAnalysis.style !== learningStyle) {
                setLearningStyle(styleAnalysis.style);
            }

            // Add agent response
            const agentMessage = {
                text: response,
                sender: 'agent',
                style: styleAnalysis.style
            };
            setMessages(prev => [...prev, agentMessage]);
        } catch (error) {
            console.error('Error in chat:', error);
            const errorMessage = {
                text: "I'm having trouble connecting right now. Please try again later.",
                sender: 'agent',
                isError: true
            };
            setMessages(prev => [...prev, errorMessage]);
        } finally {
            setIsTyping(false);
        }
    };

    return (
        <div className="chat-agent">
            <button 
                className="chat-toggle"
                onClick={() => setIsOpen(!isOpen)}
            >
                {isOpen ? 'âœ•' : 'ðŸ’¬'}
            </button>

            {isOpen && (
                <div className="chat-window">
                    <div className="chat-header">
                        <h3>Learning Assistant</h3>
                    </div>
                    
                    <div className="chat-messages">
                        {messages.map((msg, idx) => (
                            <div 
                                key={idx} 
                                className={'message ' + msg.sender}
                            >
                                {msg.text}
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>

                    <form onSubmit={handleSubmit} className="chat-input">
                        <input
                            type="text"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            placeholder="Type your message..."
                        />
                        <button type="submit">Send</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default ChatAgent;
